mapper.select:
select PK, P_SALESLEADS, P_AGENCY, P_SALESLEADSORDERSTATUS, P_RESOLVED, P_DISTRIBUTEDATE, P_DISTRIBUTER, P_AGENCYACCEPTDATE, P_AGENCYACCEPT, P_REFUSEDATE, P_SUGGESTSTORE, P_ACCEPTSTORE, P_ACCEPTDATE, P_ACCEPTBY, P_APPOINTMENTDATE, P_ACTUALDATE, P_AMOUNT, P_ORDERPICTURE, P_TELEPHONE, P_NAME, P_FITMENTDATE, P_STYLEPREFERENCE, P_COMMENT, P_BRANDPREFERENCE, P_BUDGET, P_HANDLER, P_HANDLERCOMMENT, P_CLOSEDATE, P_CLOSEREASON, P_REFUSEDATE, P_REFUSETYPE, P_REFUSEREASON, ROWNUM RN from SALESLEADSORDER where 1= 1 AND P_SALESLEADSORDERSTATUS='0'
AND P_DISTRIBUTER = ${agency}
AND ROWNUM between ${startNum} and ${pageSize}

select PK, P_SALESLEADS, P_AGENCY, P_SALESLEADSORDERSTATUS, P_RESOLVED, P_DISTRIBUTEDATE, P_DISTRIBUTER, P_AGENCYACCEPTDATE, P_AGENCYACCEPT, P_REFUSEDATE, P_SUGGESTSTORE, P_ACCEPTSTORE, P_ACCEPTBY, P_ACCEPTDATE, P_APPOINTMENTDATE, P_ACTUALDATE, P_AMOUNT, P_ORDERPICTURE, P_TELEPHONE, P_NAME, P_FITMENTDATE, P_STYLEPREFERENCE, P_COMMENT, P_BRANDPREFERENCE, P_BUDGET, P_HANDLER, P_HANDLERCOMMENT, P_CLOSEDATE, P_CLOSEREASON, P_REFUSEDATE, P_REFUSETYPE, P_REFUSEREASON, ROWNUM RN from SALESLEADSORDER where 1= 1 AND P_SALESLEADSORDERSTATUS='10' or P_SALESLEADSORDERSTATUS='15' or P_SALESLEADSORDERSTATUS='20'
AND P_DISTRIBUTER = #{distributorId}
AND ROWNUM between ${startNum} and ${pageSize}

select PK, P_NAME, P_TELEPHONE, P_CUSADDRESS, P_COMMENT, P_SERVERADDRESS, P_SERVERPURPOSE, P_SERVICETITLE, P_FITMENTDATE, P_STYLEPREFERENCE, P_BUDGET, P_BRANDPREFERENCE, P_HANDLERCOMMENT, P_SALESLEADSORDERSTATUS, P_DISTRIBUTEDATE, P_DISTRIBUTER, P_AGENCYOPERATEDATE, P_AGENCYACCEPT, P_SUGGESTSTORE, P_ACCEPTDATE, P_APPOINTMENTDATE, P_CLOSEDATE, P_AMOUNT, P_CALLSERVICETEL, P_REFUSEDATE, P_REFUSETYPE, P_REFUSEREASON from SALESLEADSORDER where 1= 1 AND P_SALESLEADSORDERSTATUS='10'
and P_ACCEPTSTORE=${storeId}
and ${startNum} < rownum and rownum < ${pageSize}+${startNum}+1

select PK, P_NAME, P_TELEPHONE, P_CUSADDRESS, P_COMMENT, P_SERVERADDRESS, P_SERVERPURPOSE, P_SERVICETITLE, P_FITMENTDATE, P_STYLEPREFERENCE, P_BUDGET, P_BRANDPREFERENCE, P_HANDLERCOMMENT, P_SALESLEADSORDERSTATUS, P_DISTRIBUTEDATE, P_DISTRIBUTER, P_AGENCYOPERATEDATE, P_AGENCYACCEPT, P_SUGGESTSTORE, P_ACCEPTDATE, P_APPOINTMENTDATE, P_CLOSEDATE, P_AMOUNT, P_CALLSERVICETEL, P_REFUSEDATE, P_REFUSETYPE, P_REFUSEREASON from SALESLEADSORDER where 1= 1 AND P_SALESLEADSORDERSTATUS='25'
and P_DISTRIBUTER=${distributorId}
and P_DISTRIBUTEDATE between #{distributeTime.begin} and #{distributeTime.end}
and P_ACCEPTSTORE=${storeId}
and P_SALESLEADS=${saleLeadId}
and P_NAME=${clientName}
and P_TELEPHONE=${clientTel}
and ${startNum} < rownum and rownum < ${pageSize}+${startNum}+1


left join agency a on s.p_agency = a .pk where  p_salesleadsorderstatus='15'

	 and s.p_acceptby=${acceptorId}


	and ${startNum} < rownum and rownum < ${pageSize}+${startNum}+1


left join agency a on s.p_agency = a .pk where P_SALESLEADSORDERSTATUS='25' 

and s.p_acceptby=${acceptorId}


and ${startNum} < rownum and rownum < ${pageSize}+${startNum}+1
